    public static long solution(int[] a) {
        long result = 0;

        long[] offset = new long[a.length];

        for (int i = 0; i < a.length; i++) {
            offset[i] = calcOffset(Integer.toString(a[i]).length());
        }


        for (int i = 0; i < a.length; i++) {
            for (int j = 0; j < a.length; j++) {
                result += a[i]*offset[j] + a[j];
            }
        }

        return result;
    }

    public static long calcOffset(int size) {
        switch (size) {
            case 0:
                return 0;
            case 1:
                return 10;
            case 2:
                return 100;
            case 3:
                return 1000;
            case 4:
                return 10000;
            case 5:
                return 100000;
            case 6:
                return 1000000;
            case 7:
                return 10000000;
        }

        return 0;
    }
